ata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spbuildApprove' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spbuildApprove]    Script Date: 19-06-2023 8:50:01 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spbuildApprove]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@user nvarchar(max) \r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\t--declare @user nvarchar(max) ='MO630208'\r\n\tSET NOCOUNT ON;\r\n\tselect * from(SELECT empid,   \r\n\t  isnull([L1],'') as 'L1', isnull([L2],(select LevelApprove from StdApprove where EMPID=[L1]))  as 'L2'\r\n\tFROM  \r\n\t(\r\n\t  SELECT Position, LevelApprove,empid   \r\n\t  FROM StdApprove\r\n\t) AS SourceTable  \r\n\tPIVOT  \r\n\t(  \r\n\t  max(LevelApprove)  \r\n\t  FOR Position IN ([L1], [L2])  \r\n\t) AS PivotTable) #a where EMPID=@user;\r\nEND\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spbuildApprove' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spBuildCosting]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spBuildCosting' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spBuildCosting]    Script Date: 19-06-2023 8:50:01 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spBuildCosting]\r\n\t@GeID nvarchar(max),\r\n\t@user nvarchar(max) ,\r\n\t@param nvarchar(max) \r\nAS\r\nBEGIN\r\n\t--declare @GeID nvarchar(max)=29399,@user nvarchar(max)='fo5910155',@param nvarchar(max)='43335,43336,43337'\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\r\n\tdeclare\t@Code nvarchar(max),\r\n\t@Costing nvarchar(max),\r\n\t@Company nvarchar(max),\r\n\t@CustomerPrice nvarchar(max),\r\n\t@VarietyPack nvarchar(max),\r\n\t@Units nvarchar(max),\r\n    @TotalPack nvarchar(max),\r\n    @PackingStyle nvarchar(max),\r\n\t@TechnicalID nvarchar(max),@request nvarchar(50),@from date,@to date,@UserType nvarchar(max)\r\n\tselect top 1 @VarietyPack=t.VarietyPack,@Units=isnull([Units],''),@PackingStyle=isnull(PackingStyle,'') from TransEditCosting t\r\n\twhere t.RequestNo=@GeID and isnull(t.Units,'')<>''\r\n\tupdate TransEditCosting set Result=case when Result is null  then '' else Result end,\r\n\tSiteId=case when len(SiteId)=1 then 0 \r\n\twhen Result='' then 0 \r\n\telse SiteId end where RequestNo=@GeID\r\n\tinsert into MasHistory values (@GeID,@User,2,getdate(),NULL,NULL,'TransTechnical')\r\n\tselect @TechnicalID=b.ID,@Code=b.RequestNo,@Company=b.Company,\r\n\t@CustomerPrice=b.CustomPrice,@from=cast(RequestDate as date),@to=cast(RequireDate as date),@UserType=usertype from TransTechnical b where b.Id=@GeID\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tSET NOCOUNT ON;\r\n\tselect #a.ID,MarketingNumber,CostNo,#a.PackSize,#a.Packaging,Netweight,b.Code,\r\n\t#a.Customer,CanSize,b.Formula into #temp\r\n\tfrom (select a.ID,a.Customer,a.PackSize,a.CanSize,a.Packaging,Netweight,a.MarketingNumber,a.StatusApp from TransCostingHeader a where a.id in \r\n\t(select max(convert(int,b.RequestNo)) from TransFormulaHeader b group by b.Code)) #a\r\n\tleft join TransFormulaHeader b on #a.ID=b.RequestNo where #a.StatusApp in (0,4,8) and CostNo !=''\r\n\t\r\n\t--select max(convert(int,b.RequestNo)),b.Code from TransFormulaHeader b where code in ('3JCCM822R2DB3PPSPL','3HAOM822R2DB3PPSPL','3HSSX822R2DB3PPSPL') group by b.Code\r\n\r\n\t--1.update costing\r\n\tIf Object_ID('tempdb..#newtable')  is not null  drop table #newtable\r\n\tupdate t set t.CostingSheet= (case when t.CostingSheet='' or t.CostingSheet is null then\r\n\t(select top 1 #temp.CostNo from #temp where #temp.Id in(select max(b.id) from #temp b where b.Code= t.Material) and #temp.Code= t.Material) else t.CostingSheet end)\r\n\t--select t.CostingSheet, a.CostNo\r\n\tfrom TransEditCosting t\r\n\twhere t.RequestNo=@GeID and isnull(t.CostingSheet,'')='' and isnull(t.SiteId,0) =0 and t.ID in (select value from dbo.FNC_SPLIT(@param,','))\r\n\t--2 maping\r\n\tselect t.ID as 'tID',t.CostingSheet,b.*\r\n\t,0 as [siteId] into #newtable from TransEditCosting t \r\n\tleft join #temp b on t.CostingSheet = b.CostNo where t.RequestNo=@GeID and isnull(t.SiteId,0) =0 and t.ID in (select value from dbo.FNC_SPLIT(@param,','))\r\n\t--select * from #newtable\r\n\tdeclare @cur int,@Netweight nvarchar(max),@Packaging nvarchar(max),@PackSize nvarchar(max),@Customer nvarchar(max),@CanSize nvarchar(max)\r\n\tdeclare cur_temp CURSOR FOR\r\n\t--select value from dbo.FNC_SPLIT(@cols,',')\r\n\t--3 create \r\n\tselect count(t.CostingSheet)c,t.Netweight,t.Packaging,t.PackSize,t.Customer,t.CanSize from #newtable t\r\n\tgroup by t.Netweight,t.Packaging,t.PackSize,t.Customer,t.CanSize\r\n\topen cur_temp\r\n\r\n\tFETCH NEXT FROM cur_temp INTO @cur,@Netweight,@Packaging,@PackSize,@Customer,@CanSize\r\n\tWHILE @@FETCH_STATUS = 0\r\n\tBEGIN\r\n\t--declare @Company nvarchar(max)='1031',@UserType nvarchar(max)=1\r\n\tdeclare @x datetime= GETDATE(),@runid nvarchar(max),@NewID int ,@NamingCode nvarchar(max),@fix int,@month nvarchar(max)   = month(getdate())\r\n\t--select CONVERT(int,CONVERT(CHAR(4), @x, 120))\r\n\tIF CONVERT(int,CONVERT(CHAR(4), @x, 120)) < 2500\r\n\tSET @x=DATEADD(yyyy,543,@x)\r\n\t--select FORMAT(@x, 'yy')\r\n\tset @NamingCode = (select top 1 NamingCode from MasPlant where Code =@Company and dbo.fnc_checktype(usertype,@UserType)>0)\r\n\t--set @runid = @NamingCode+FORMAT(@x, 'yy')+(\r\n\t--select format(isnull(max(right(MarketingNumber,4)),0)+1, '0000') from  TransCostingHeader \r\n\t--where substring(MarketingNumber,3,2)=FORMAT(@x, 'yy') and Company=@Company --and substring(MarketingNumber,5,1)='5'\r\n\t--)\r\n\tIF CONVERT(int,CONVERT(CHAR(4), @x, 120)) < 2500\r\n\tSET @x=DATEADD(yyyy,543,@x)\r\n\t--select FORMAT(@x, 'yy')\r\n\tset @fix = (select SUBSTRING(@month,1,1))\r\n\tif(@month='10' or @month='11' or @month='12')\r\n\t\tset @fix='1'\r\n\tset @runid = @NamingCode+FORMAT(@x, 'yy')+(\r\n\tselect format(@fix\t,'0')+''+format(isnull(max(right(MarketingNumber,3)),0)+1, '000') from  TransCostingHeader \r\n\twhere substring(MarketingNumber,3,3)=concat(FORMAT(@x, 'yy'),@fix) and requestno<>0 and \r\n\tsubstring(MarketingNumber,1,2) = @NamingCode)\r\n\r\n\tprint @runid;\r\n\r\n\tDECLARE @myid uniqueidentifier = NEWID(),@id nvarchar(max)\r\n\t--SELECT CONVERT(nvarchar(max), @myid) AS 'NEWID';\r\n\tprint @myid;  \r\n\tset @id=(select top 1 Id from #newtable where Netweight=@Netweight and Packaging=@Packaging and PackSize=@PackSize \r\n\t  and isnull(Customer,'')=@Customer and CanSize=@CanSize)\r\n\t--select * from TransCostingHeader\r\n\tinsert into TransCostingHeader\r\n\tselect\r\n\t@TechnicalID,@runid,RDNumber,Company,\r\n\t(case when len(@Units)>0 then @Units else  PackSize end),\r\n\tgetdate(),@user,null,null,@myid,Remark,\r\n\t(case when len(@PackingStyle)>0 then @PackingStyle else  CanSize end),Packaging,2,0\r\n      ,isnull((select top 1 Rate from MasExchangeRat where Company=@Company and ExchangeType=@CustomerPrice and @from between Validfrom and Validto),0) as 'ExchangeRate'\r\n\t  ,Netweight,0,0,customer ,@From\r\n      ,@To\r\n      ,@UserType,@VarietyPack --ExchangeRate\r\n\t  from TransCostingHeader where ID \r\n\t  in (@id) \r\n\r\n\t  Set @NewID =(SELECT CAST(scope_identity() AS int))\r\n\t  --Exec spcreateapprove @NewID ,@user,1\r\n\t  update #newtable set [siteId]=@NewID where Netweight=@Netweight and Packaging=@Packaging and PackSize=@PackSize\r\n\t  and isnull(Customer,'')=@Customer and CanSize=@CanSize\r\n\r\n\tFETCH NEXT FROM cur_temp INTO @cur,@Netweight,@Packaging,@PackSize,@Customer,@CanSize\r\n\tEND\r\n\r\n\tCLOSE cur_temp\r\n\tDEALLOCATE cur_temp\r\n\tupdate t set t.SiteId=b.SiteId\r\n\tfrom TransEditCosting t left join #newtable b on t.ID=b.tID where t.RequestNo=@GeID and isnull(t.SiteId,0) =0\r\n\tand t.ID in (select value from dbo.FNC_SPLIT(@param,','))\r\n\tselect * from #newtable\r\nEND\r\n \r\n \r\n","GO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spBuildCosting' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spbuildMapCosting]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spbuildMapCosting' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spbuildMapCosting]    Script Date: 19-06-2023 8:50:01 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","CREATE PROCEDURE [dbo].[spbuildMapCosting] \r\n\t@value nvarchar(max),\r\n\t@key nvarchar(1),\r\n\t@user nvarchar(max),\r\n\t@GeID nvarchar(max),\r\n\t@ID nvarchar(max),\r\n\t@SiteId nvarchar(max)\r\nAS\r\nBEGIN\r\n\t--declare @value nvarchar(max)='179',@key nvarchar(1)=1,@user nvarchar(max)='fo5910155',@GeID nvarchar(max)='255',@ID nvarchar(max)='7486',@SiteId nvarchar(max)='7522'\r\n\tdeclare @NewID int=0\r\n\tdeclare @result nvarchar(max)=(select isnull(Result,'0') from TransEditCosting where ID=@value)\r\n\tIF (@result <> '0') GOTO jumploop\r\n\tdeclare\t@Code nvarchar(max),\r\n\t@Costing nvarchar(max),\r\n\t@Company nvarchar(3),\r\n\t@TechnicalID nvarchar(max)\r\n\tselect @TechnicalID=b.ID,@Code=b.RequestNo,@Company=b.Company from TransTechnical b where b.Id=@GeID\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tSET NOCOUNT ON;\r\n\tdeclare @RequestNo nvarchar(max),@runid nvarchar(max)\r\n\tset @runid = (select MarketingNumber from TransCostingHeader where ID=@SiteId)\r\n\r\n    -- Insert statements for procedure here\r\n\tset @RequestNo = @GeID\r\n\tset @NewID=@SiteId\r\n\t/*SELECT * into #temp from TransEditCosting where RequestNo=@GeID \r\n\tand SiteId=@SiteId\r\n\tDECLARE @cols AS NVARCHAR(MAX),\r\n\t@query  AS NVARCHAR(MAX);\r\n\t--SET @cols = STUFF((SELECT  ',' + QUOTENAME(formula) from #temp\r\n\tSET @cols = STUFF((SELECT distinct ',' + (Costingsheet) from #temp\r\n\t\t\t\t\tFOR XML PATH(''), TYPE\r\n\t\t\t\t).value('.', 'NVARCHAR(MAX)') \r\n\t\t\t,1,1,'')\r\n\tprint @cols;*/\r\n\r\ndeclare @index int = 1\r\ndeclare @cur nvarchar(max),@SellingUnit nvarchar(max),@VarietyPack nvarchar(max)\r\ndeclare cur_temp CURSOR FOR\r\n--select distinct value from dbo.FNC_SPLIT(@cols,',')\r\nSELECT Costingsheet,isnull(SellingUnit,0),isnull(VarietyPack,'') from TransEditCosting where RequestNo=@GeID \r\n\tand SiteId=@SiteId\r\nopen cur_temp\r\n\r\nFETCH NEXT FROM cur_temp INTO @cur,@SellingUnit,@VarietyPack\r\nWHILE @@FETCH_STATUS = 0\r\nBEGIN\r\n\t  declare @formula nvarchar(max),@PackSize nvarchar(max),@Packaging nvarchar(max),@NW nvarchar(max),@Unit nvarchar(max) \r\n\t  select @formula=Formula,@ID=RequestNo from TransFormulaHeader where CostNo in (@cur)\r\n\t  select @packSize=PackSize,@Packaging=Packaging,@NW=(\r\n\t  case when CHARINDEX('|',Netweight)>0 then SUBSTRING('80|Grams',1,CHARINDEX('|',Netweight)-1) else '0' end)\r\n\t  from TransCostingHeader where id=@ID\r\n\t  --select top 10 * from TransFormulaHeader order by id desc\r\n\t  --select CHARINDEX('|','80|Grams'),SUBSTRING('80|Grams',1,CHARINDEX('|','80|Grams')-1)\r\n\t  insert into TransFormulaHeader\r\n\t  select Name,Customer,Code,RefSamples,@index,1,0,@runid+ format(@index,'00'),Revised,'','',@SellingUnit,RequestNo,isnull(Packaging,@Packaging),isnull(NW,@NW),\r\n\t  isnull([PackSize],@packSize),@NewID \r\n\t  from TransFormulaHeader where CostNo in (@cur) and RequestNo=@ID\r\n\r\n-- formula\r\n\tinsert into TransFormula\r\n\t  select Component,SubType,[Description],Material,Result,Yield,RawMaterial,Name\r\n      ,PriceOfUnit\r\n      ,Currency\r\n      ,Unit\r\n      ,ExchangeRate\r\n      ,BaseUnit\r\n      ,PriceOfCarton\r\n      ,@index,IsActive,Remark,LBOh,LBRate,@NewID from TransFormula where RequestNo=@ID and Formula in (@formula)\r\n\t-- costing\r\n\tif(len(@VarietyPack)>0) begin\r\n\tinsert into TransCosting\r\n\tselect Component,SAPMaterial,[Description],@SellingUnit,PriceUnit,(convert(float,@SellingUnit)*convert(float,PriceUnit)),Per,SellingUnit,Loss,getdate(),@user,null,null,@index,@NewID \r\n\t\tFROM TransCosting where RequestNo=@ID and Formula in (@formula) --and lower(Component) in ('upcharge','primary packaging')\r\n\t\t--select * from TransCosting where lower(Component) in ('upcharge','primary packaging')\r\n\tend \r\n\telse begin\r\n\tinsert into TransCosting\r\n\tselect Component,SAPMaterial,[Description],Quantity,PriceUnit,Amount,Per,SellingUnit,Loss,getdate(),@user,null,null,@index,@NewID \r\n\t\tFROM TransCosting where RequestNo=@ID and Formula in (@formula)\r\n\tend\r\n\tupdate TransEditCosting set Result=@runid+''+ format(@index,'00') \r\n\twhere CostingSheet in (@cur)\r\n\tand RequestNo=@GeID\r\n\tset @index= @index + 1\r\n\tFETCH NEXT FROM cur_temp INTO @cur,@SellingUnit,@VarietyPack\r\nEND\r\n\r\nCLOSE cur_temp\r\nDEALLOCATE cur_temp\r\n----update price\r\n--\tDECLARE @curid int,@Material nvarchar(max)\r\n--\tDECLARE cur CURSOR FOR SEL