x),formula nvarchar(max),n int)\r\n\tdeclare @ID int,@runid nvarchar(max),@x datetime= GETDATE();\r\n\tIF CONVERT(int,CONVERT(CHAR(4), @x, 120)) < 2500\r\n\tSET @x=DATEADD(yyyy,543,@x) \r\n    -- Insert statements for procedure here\r\n\tif len(@CostingNo)=8\r\n\tbegin\r\n\tdeclare @n nvarchar(max)=(select id from TransCostingHeader where MarketingNumber= @CostingNo\r\n\tand StatusApp <>7)\r\n\t--insert into @table(costingno)values(@CostingNo)\r\n\tinsert into @table(formula,n)\r\n\tselect formula,requestno from TransFormula where RequestNo=@n\r\n\tgroup by Formula,RequestNo\r\n\tupdate @table set costingno=@CostingNo\r\n\tend\r\n\telse if len(@CostingNo)=10\r\n\tinsert into @table values(\r\n\tleft(@CostingNo,8),\r\n\tsubstring(@costingno,9,2),0)\r\n\tDECLARE @myid uniqueidentifier = NEWID();  \r\n\tupdate t set n=a.ID\r\n\tfrom @table t left join TransCostingHeader a on a.MarketingNumber=t.costingno\r\n\tset @runid = (select fn from MasCompany where substring(Code,1,3) in (select substring(company,1,3) from TransCostingHeader where MarketingNumber=left(@CostingNo,8)))+FORMAT(@x, 'yy')+(\r\n\tselect format(isnull(max(right(MarketingNumber,4)),0)+1, '0000') from  TransCostingHeader \r\n\twhere substring(MarketingNumber,3,2)=FORMAT(@x, 'yy'))\r\n\tprint @runid;\r\n\t--select * from @table\r\n\tinsert into TransCostingHeader\r\n\tselect RequestNo=@RequestNo\r\n      ,MarketingNumber=@runid\r\n      ,RDNumber\r\n      ,Company\r\n      ,PackSize\r\n      ,CreateOn=GETDATE()\r\n      ,CreateBy=@Requester\r\n      ,ModifyOn=null\r\n      ,ModifyBy=null\r\n      ,UniqueColumn=CONVERT(nvarchar(max), @myid)\r\n      ,Remark\r\n      ,CanSize\r\n      ,Packaging\r\n      ,StatusApp=0\r\n      ,Revised\r\n      ,ExchangeRate\r\n\t  ,Netweight\r\n      ,IsActive,0,Customer,[From]\r\n      ,[To]\r\n      ,[UserType] from TransCostingHeader where ID \r\n\tin (select n from @table) and StatusApp <>7\r\n\tSET @ID = (SELECT CAST(scope_identity() AS int))\r\n\t\tExec spcreateapprove @ID ,@Requester ,1\r\n--Formula\r\n\tinsert into TransFormula\r\n\tSELECT Component\r\n      ,SubType\r\n      ,[Description]\r\n      ,Material\r\n      ,Result\r\n      ,Yield\r\n      ,RawMaterial\r\n      ,Name\r\n      ,PriceOfUnit\r\n      ,Currency\r\n      ,Unit\r\n      ,ExchangeRate\r\n      ,BaseUnit\r\n      ,PriceOfCarton\r\n      ,a.Formula\r\n      ,IsActive\r\n      ,Remark\r\n      ,LBOh\r\n      ,LBRate\r\n      ,RequestNo=@ID\r\n  FROM TransFormula a inner join @table b on b.n=a.RequestNo and cast(b.formula as int)=a.Formula --and IsActive=0\r\n  --Margin,Upcharge\r\n  insert into TransCosting\r\n  SELECT Component\r\n      ,SAPMaterial\r\n      ,[Description]\r\n      ,Quantity\r\n      ,PriceUnit\r\n      ,Amount\r\n      ,Per\r\n      ,SellingUnit\r\n      ,Loss\r\n      ,CreateOn=getdate()\r\n      ,CreateBy=@Requester\r\n      ,ModifyOn=null\r\n      ,ModifyBy=null\r\n      ,a.Formula\r\n      ,RequestNo=@ID\r\n  FROM TransCosting a inner join @table b on b.n=a.RequestNo and cast(b.formula as int)=a.Formula \r\n  select @ID\r\nEND\r\n \r\n\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spcopyCostingsheet' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spcopyQuotation]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spcopyQuotation' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spcopyQuotation]    Script Date: 19-06-2023 8:50:01 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spcopyQuotation] \r\n\t-- Add the parameters for the stored procedure here\r\n\t@Id nvarchar(max),\r\n\t@Requester nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\t--declare @Id nvarchar(max)=14413,@Requester nvarchar(max)='FO5910155'\r\n\tSET NOCOUNT ON;\r\n\tdeclare @RequestNo nvarchar(max)\r\n\t--add new\r\n\tdeclare @x datetime= GETDATE(),@runid nvarchar(max)\r\n\t--select CONVERT(int,CONVERT(CHAR(4), @x, 120))\r\n\tIF CONVERT(int,CONVERT(CHAR(4), @x, 120)) < 2500\r\n\tSET @x=DATEADD(yyyy,543,@x)\r\n\t--select FORMAT(@x, 'yy')\r\n\tset @runid= FORMAT(GetDate(), 'yyyyMM') +''+ \r\n\t(SELECT format(isnull(max(right(RequestNo,5)),0)+1, '00000') FROM TransQuotationHeader\r\n\twhere SUBSTRING(RequestNo,1,6)=FORMAT(GetDate(), 'yyyyMM'))\r\n\tprint @runid;\r\n\tDECLARE @myid uniqueidentifier = NEWID();  \r\n    -- Insert statements for procedure here\r\n\t--SELECT @StatusApp=StatusApp from TransCostingHeader where ID=@Id\r\n\tinsert into TransQuotationHeader \r\n\tselect @Requester,Getdate(),null,null\r\n\t\t  ,[Commission]\r\n\t\t  ,[Brand]\r\n\t\t  ,[Incoterm]\r\n\t\t  ,[Route]\r\n\t\t  ,[Size]\r\n\t\t  ,[PaymentTerm]\r\n\t\t  ,[Interest]\r\n\t\t  ,[Freight]\r\n\t\t  ,[Insurance]\r\n\t\t  ,[Remark]\r\n\t\t  ,[Customer]\r\n\t\t  ,[ShipTo]\r\n\t\t  ,0\r\n\t\t  ,[Currency]\r\n\t\t  ,ExchangeRate\r\n\t\t  ,@runid,NEWID() from TransQuotationHeader where ID=@Id\r\n\tSET @RequestNo = (SELECT CAST(scope_identity() AS int))\r\n\r\ndeclare @fn nvarchar(max)\r\ndeclare cur_table CURSOR FOR\r\nSELECT value,@runid FROM dbo.FNC_SPLIT('TransQuotationItems',',')\r\nopen cur_table\r\nFETCH NEXT FROM cur_table INTO @fn,@runid\r\nWHILE @@FETCH_STATUS = 0\r\nBEGIN\r\nIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tSELECT t.name AS table_name,\r\n\tSCHEMA_NAME(schema_id) AS schema_name,\r\n\tc.name AS column_name,column_id into #temp\r\n\tFROM sys.tables AS t\r\n\tINNER JOIN sys.columns c ON t.OBJECT_ID = c.OBJECT_ID\r\n\tWHERE t.name in (''+@fn+'') and c.name not in ('Id','SubID')\r\n\tORDER BY c.column_id;\r\n\r\n\tDECLARE @cols AS NVARCHAR(MAX),\r\n\t\t@query  AS NVARCHAR(MAX);\r\n\tSET @cols = STUFF((SELECT ',' + QUOTENAME(c.column_name) \r\n            FROM #temp c ORDER BY c.column_id\r\n            FOR XML PATH(''), TYPE\r\n            ).value('.', 'NVARCHAR(MAX)') \r\n        ,1,1,'')\r\n\t\tset @query='insert '+@fn+' select '+@cols+','+@RequestNo+' from '+ @fn +' where SubID='+@Id\r\n\t\tprint @query;\r\n\t\texecute(@query)\r\n\t\t--reset \r\n\t\tupdate TransQuotationItems set IsActive=0,ActionDate=null where SubID=@Id\r\n\t\tFETCH NEXT FROM cur_table INTO @fn,@runid\r\nEND\r\nCLOSE cur_table\r\nDEALLOCATE cur_table\r\n \r\nselect @RequestNo \r\nEND\r\n\r\n \r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spcopyQuotation' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spCopyStdItems]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spCopyStdItems' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spCopyStdItems]    Script Date: 19-06-2023 8:50:01 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spCopyStdItems] \r\n\t-- Add the parameters for the stored procedure here\r\n\t@RequestNo nvarchar(max),\r\n\t@SubIDNew nvarchar(max),\r\n\t@Id nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\r\ndeclare @fn nvarchar(max)\r\ndeclare cur_temp CURSOR FOR\r\nSELECT value FROM dbo.FNC_SPLIT('TransUpCharge,TransUtilize',',')\r\nopen cur_temp\r\nFETCH NEXT FROM cur_temp INTO @fn\r\nWHILE @@FETCH_STATUS = 0\r\nBEGIN\r\nIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tSELECT t.name AS table_name,\r\n\tSCHEMA_NAME(schema_id) AS schema_name,\r\n\tc.name AS column_name,column_id into #temp\r\n\tFROM sys.tables AS t\r\n\tINNER JOIN sys.columns c ON t.OBJECT_ID = c.OBJECT_ID\r\n\tWHERE t.name in (''+@fn+'') and c.name not in ('Id','SubID','RequestNo')\r\n\tORDER BY c.column_id;\r\n\r\n\tDECLARE @cols AS NVARCHAR(MAX),\r\n\t\t@query  AS NVARCHAR(MAX);\r\n\tSET @cols = STUFF((SELECT ',' + QUOTENAME(c.column_name) \r\n            FROM #temp c ORDER BY c.column_id\r\n            FOR XML PATH(''), TYPE\r\n            ).value('.', 'NVARCHAR(MAX)') \r\n        ,1,1,'')\r\n\t\tset @query='insert '+@fn+' select '+@cols+','+@SubIDNew+','+@RequestNo+' from '+ @fn +' where SubID='+@Id\r\n\t\tprint @query;\r\n\t\texecute(@query)\r\n\t\tFETCH NEXT FROM cur_temp INTO @fn\r\nEND\r\nCLOSE cur_temp\r\nDEALLOCATE cur_temp\r\nEND\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spCopyStdItems' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spCopyTechnical]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spCopyTechnical' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spCopyTechnical]    Script Date: 19-06-2023 8:50:01 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- ============