 t.name in ('TransTunaStdItems') and c.name not in ('Id','RequestNo')\r\n\tORDER BY c.column_id;\r\n\t--select * from TransTunaStdItems\r\n\tDECLARE @cols AS NVARCHAR(MAX),@SubIDNew NVARCHAR(MAX),@query  AS NVARCHAR(MAX);\r\n\t\tinsert TransTunaStdItems select [Material],[Name]\r\n      ,[Utilize]\r\n      ,[From]\r\n      ,[To]\r\n      ,[RawMaterial]\r\n\t  ,SpecialFishPrice\r\n\t  ,PackSize\r\n\t  ,Yield\r\n\t  ,FillWeight\r\n      ,[SubContainers]\r\n      ,[Media]\r\n      ,[Packaging]\r\n      ,[LOHCost]\r\n      ,[PackingStyle]\r\n\t  ,SecPackaging\r\n      ,[Upcharge]\r\n      ,[Commission]\r\n      ,[OverPrice]\r\n      ,[OverType]\r\n      ,[Pacifical]\r\n      ,[MSC]\r\n      ,[Margin]\r\n\t  ,[Authorized_price]\r\n\t  ,[Bidprice]\r\n\t  ,[Margin]\r\n      ,[MinPrice]\r\n      ,[OfferPrice],'',@RequestNo from TransTunaStdItems where Id=@RowID\r\n\t\tSET @SubIDNew= (SELECT CAST(scope_identity() AS int))\r\n\t\texec spCopyStdItems @RequestNo,@SubIDNew,@RowID \r\n\t\tFETCH NEXT FROM cur_table INTO @RowID,@RequestNo\r\nEND\r\nCLOSE cur_table\r\nDEALLOCATE cur_table\r\n \r\nselect @RequestNo \r\nEND\r\n\r\n--select * from TransTunaStdItems \r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spcopyTunaStd' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spCreateApprove]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spCreateApprove' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spCreateApprove]    Script Date: 19-06-2023 8:50:01 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n--