ase[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLaborOverhead' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetLaborOverhead]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE Procedure [dbo].[spGetLaborOverhead]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Company nvarchar(max),\r\n\t@Packaging nvarchar(max),\r\n\t@NetWeight nvarchar(max),\r\n\t@PackSize nvarchar(max),\r\n\t@UserType nvarchar(max)\r\nAS\r\nBEGIN\r\n\t--declare @Company nvarchar(max)='103',\t@Packaging nvarchar(max)='Pouch',@NetWeight nvarchar(max)=40,@PackSize nvarchar(max)=24,@UserType nvarchar(max)=0\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\r\n\tIf Object_ID('tempdb..#table')  is not null  drop table #table\r\n\tSET NOCOUNT ON;\r\n\tif(@Packaging='Cup')\r\n\tset @Packaging='Cup,Plastic container'\r\n\tselect b.LBOh ,@NetWeight as 'NetWeight' into #table from MasPrimary b where b.Name in (select value  from dbo.FNC_SPLIT(@Packaging,',')) and dbo.fnc_checktype(b.usertype,@UserType)>0\r\n\tselect b.ID,((b.LBRate / convert(float,b.PackSize))* @PackSize)LBRate,\r\n\tLBCode,\r\n\tLBName,\r\n\t@PackSize as 'PackSize',\r\n\tLBType,\r\n\tCurrency\r\n\tfrom #table a left join MasLaborOverhead b on a.Lboh = substring(LBCode,1,2)\r\n\tand NetWeight between convert(float,lbmin) and convert(float,lbmax) \r\n\t--and PackSize=@PackSize\r\n\twhere Company=@Company order by LBName \r\nEND\r\n--select SUBSTRING('02001',1,2)\r\n--select * from MasLaborOverhead\r\n\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLaborOverhead' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetLacquer]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLacquer' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetLacquer]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spGetLacquer]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Primary nvarchar(max),\r\n\t@usertype nvarchar(max)\r\nAS\r\nBEGIN\r\n    --declare @Primary nvarchar(max)='Can',@usertype nvarchar(max)='0,1'\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n    select ID,Name,UserType from(Select * from MasLacquer where dbo.fnc_checktype(usertype,@usertype)>0 and @Primary\r\n    in (select value from dbo.FNC_SPLIT(tcode,',')))#a\r\nEND\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLacquer' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetLB]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLB' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetLB]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE Procedure [dbo].[spGetLB]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Company nvarchar(max),\r\n\t@SubID nvarchar(max)\r\n\r\nAS\r\nBEGIN\r\n\t--declare @Company nvarchar(max)='1012',\t@SubID nvarchar(max)='2785' \r\n \r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tdeclare @Packaging nvarchar(max),\r\n\t@NetWeight nvarchar(max),\r\n\t@PackSize nvarchar(max),\r\n\t@UserType nvarchar(max),\r\n\t@RequestNo nvarchar(max),\r\n\t@BU nvarchar(max)\r\n\r\n\tselect @NetWeight=NetWeight,@PackSize=PackSize,@RequestNo=RequestNo from TransProductList where id=@subid\r\n\tselect @UserType=UserType,@BU=PetCategory from TransTechnical where id=@RequestNo\r\n\tprint @UserType;\r\n\tSET NOCOUNT ON;\r\n\tif(@Packaging='Cup')\r\n\tset @Packaging='Cup,Plastic container'\r\n \r\n\tIf Object_ID('tempdb..#table')  is not null  drop table #table\r\n\t--select id,LBMax,ISNUMERIC(LBMax)'LBMaxx' into #table from MasLaborOverhead\r\n\t--select #table.id from #table where LBMaxx='0'\r\n\t--select * from MasLaborOverhead where id=14232\r\n\r\n\tselect b.ID,b.LBRate,--((b.LBRate / convert(float,b.PackSize))* @PackSize)LBRate,\r\n\tLBCode,\r\n\tLBName,\r\n\t@PackSize as 'PackSize',\r\n\tLBType,\r\n\tCurrency\r\n\tfrom MasLaborOverhead b where b.PackSize = @PackSize\r\n\tand @Company like Company+'%'\r\n\tand @NetWeight between \r\n\t(case when unit='KG'  then convert(float,lbmin) * 1000 else \r\n\tconvert(float,lbmin) end) and (case when unit='KG'  then convert(float,lbmax) * 1000 else convert(float,lbmax) end) \r\n\t--and PackSize=@PackSize\r\n\tand dbo.fnc_checktype(@UserType,isnull(BU,''))>0\r\n\tand dbo.fnc_checktype(@BU,isnull(Category,''))>0\r\n\t order by LBName \r\nEND\r\n--select SUBSTRING('02001',1,2)\r\n--delete MasLaborOverhead where company='101' and  (LBMax = '1003;1004')\r\n--select convert(float,'0.08')\r\n \r\n\r\n\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLB' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetLevelApprove]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLevelApprove' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetLevelApprove]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","\r\nCREATE PROCEDURE [dbo].[spGetLevelApprove]\r\n\t@Id nvarchar(max),\r\n\t@table nvarchar(max),\r\n\t@username nvarchar(max),\r\n\t@zone nvarchar(max)\r\nAS\r\nBEGIN\r\n\r\n--declare @Id nvarchar(max),@table nvarchar(max)='level1',\t@username nvarchar(max)='MO630208',\t@zone nvarchar(max)='USA'\r\ndeclare @temp table(empid nvarchar(30),l1 nvarchar(max),l2 nvarchar(max))\r\ninsert into @temp\r\nexec spbuildApprove @username\r\n--select * from @temp\r\nIf Object_ID('tempdb..#ulogin')  is not null  drop table #ulogin\r\nselect * into #ulogin from ulogin\r\ndeclare @position nvarchar(max)= (select top 1 Sublevel from MasApprovAssign b where b.EmpId=@username)\r\nif(@table='level1') begin\r\n\tdeclare @countemp int =(select count(a.empid)\r\n\tfrom StdAssignZone a where a.empid in (select value from dbo.FNC_SPLIT((select s.LevelApprove from StdApprove s where s.EMPID=@username),';')) and a.zone like '%'+@zone+'%')\r\n\t----------------\r\n\tif @position = 15\r\n\tselect distinct user_name as empid,concat(b.firstname,' ',b.lastname)'name' from #ulogin b where b.[user_name] in (\r\n\t\tselect l2 from @temp)\r\n\telse\r\n\t\tselect distinct empid,(select top 1 concat(b.firstname,' ',b.lastname) from #ulogin b where b.[user_name]=#a.empid)'name' from(\r\n\t\tselect a.empid\r\n\t\tfrom StdAssignZone a where a.empid in (select value from dbo.FNC_SPLIT((select s.LevelApprove from StdApprove s where s.EMPID=@username),';')) and a.zone like \r\n\t\tcase when @countemp >0 then '%'+@zone+'%' else a.Zone end \r\n\t\tunion \tselect a.empid \r\n\t\tfrom StdAssignZone a where a.zone like '%'+@zone+'%' union \r\n\t\tselect case when l1='' then l2 else l1 end from @temp)#a where EMPID<>'' \r\n\t\t\r\n\tend\r\nif(@table='level2') begin\r\n\tdeclare @level nvarchar(max)= (select s.LevelApprove from StdApprove s where s.EMPID=@username)\r\n\tselect distinct empid,(select top 1 concat(b.firstname,' ',b.lastname) from #ulogin b where b.[user_name]=#a.empid)'name' from(\r\n\tselect a.empid\r\n\tfrom StdAssignZone a where a.empid in (select value from dbo.FNC_SPLIT((select top 1 s.LevelApprove from StdApprove s where s.EMPID in (select value from dbo.FNC_SPLIT(@level,';'))),';'))\r\n\tunion select LevelApprove from StdApprove where EMPID=@username and Position='L2'\r\n\t)#a\r\n\tend\r\nend\r\n\r\n--select * from StdApprove\r\n\r\n--select * from StdAssignZone\r\n","GO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLevelApprove' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetLoss]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetLoss' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetLoss]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Descri