re SubId=@requestno\r\n--if(@icount=0)\r\n--select * from TransQuotationCustom where costingno in (select costno from TransFormulaHeader where RequestNo=@requestno)\r\nend\r\n \r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetQuotaCustomer' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetQuotaHeader]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetQuotaHeader' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetQuotaHeader]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE Procedure [dbo].[spGetQuotaHeader]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@user_name nvarchar(max),\r\n\t@type nvarchar(max)\r\nAS\r\nBEGIN\r\n\t--declare @user_name nvarchar(max)='FO5910155',@type nvarchar(max)=0\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tIf Object_ID('tempdb..#find')  is not null  drop table #find\r\n\tselect * into #find from dbo.FindULevel(@user_name)\r\n\t--select * from #find\r\n\tSET NOCOUNT ON;\r\n\tdeclare @temp tabletype;delete @temp\r\n\tinsert into @temp\r\n\tselect * from(select editor from #find)#a\r\n\tdeclare @editor nvarchar(max)= dbo.fnc_stuff(@temp)\r\n\t--print @editor;\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tSELECT ID,\r\n\t(select top 1 firstname +' '+ lastname from ulogin where user_name=a.CreateBy)'Requester',\r\n\tCreateBy,\r\n\tCreateOn,\r\n\tBrand,\r\n\t(select top 1 Name from MasCustomer where Code=a.Customer)'Customer',\r\n\t(select top 1 Name from MasCustomer where Code=a.ShipTo)'ShipTo',\r\n\ta.PaymentTerm,\r\n\ta.Incoterm,\r\n\ta.StatusApp,\r\n\ta.RequestNo\r\n\tinto #temp from TransQuotationHeader a\r\n    -- Insert statements for procedure here\r\n\t\tif (@type=1)\r\n\t\tSELECT * from #temp a where a.ID in\r\n\t\t(select distinct(RequestNo) from MasHistory where Username in (select distinct value from dbo.FNC_SPLIT(@editor,','))\r\n\t\tand tablename in ('3','TransQuotationHeader')) \r\n\t\t\telse\r\n\t\t\tbegin\r\n\t\t\tif(select count(*) from #find where Sublevel = 3)>0\r\n\t\t\tSELECT * from #temp where StatusApp in (0) and CreateBy=@user_name\r\n\t\t\tif(select count(*) from #find where Sublevel = 4)>0\r\n\t\t\tSELECT * from #temp where StatusApp in (8)\r\n\t\t\tif(select count(*) from #find where Sublevel = 11)>0\r\n\t\t\tSELECT * from #temp where StatusApp in (9)\r\n\tend\r\nEND\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetQuotaHeader' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetQuotationItems]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetQuotationItems' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetQuotationItems]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE Procedure [dbo].[spGetQuotationItems]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Id nvarchar(max),\r\n\t@requestno nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\t--declare @requestno nvarchar(max)=31,@Id nvarchar(max)=0\r\n    -- Insert statements for procedure here\t\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tSELECT a.Name,isnull(z.Code_Adjust,a.Code) as 'Code',a.RefSamples,\r\n\tcase when isnull(RequestType,'')='V' then (select b.MarketingNumber from TransCostingHeader b where b.ID=z.RequestNo) else\r\n\ta.CostNo end as CostNo,a.RequestNo,'' as Mark,\t\r\n\tisnull(Overprice,'')'Overprice',\r\n\tisnull(Extracost,'')'Extracost',\r\n\tisnull(z.subContainers,'0') as 'SubContainers',\r\n\tisnull(z.MinPrice,'0') as 'MinPrice',\r\n\tisnull(z.ExchangeRate, (select t.ExchangeRate from TransCostingHeader t where t.ID=z.requestno) ) as 'ExchangeRate',\r\n\tcase when z.OfferPrice ='' or z.OfferPrice is null  then '0' else z.OfferPrice end  as 'OfferPrice',\r\n\tcase when OfferPrice_Adjust ='' or OfferPrice_Adjust is null  then '0' else OfferPrice_Adjust end as OfferPrice_Adjust,\r\n\r\n\t--'0' as 'Freight',\r\n\t--'0' as 'Insurance',\r\n\tisnull(RequestType,'') as RequestType,\r\n\tisnull(z.SubID,'') as SubID,\r\n\tisnull(z.CostingNo,'') as CostingNo,isnull(z.ID,0) as ID into #temp\r\n\t from  TransQuotationItems z left join TransFormulaHeader a \r\n\ton a.ID=z.CostingNo  where z.SubID=@requestno \r\n\r\n select *,\r\n\tcase when OfferPrice_Adjust ='0' then '0' else \r\n\t(case when (convert(float,OfferPrice) - convert(float,OfferPrice_Adjust)) > 0.04 then 'A'end) end as 'StatusApp',\r\n\r\n\tcase when OfferPrice_Adjust ='0' then '0' else (convert(float,OfferPrice) - convert(float,OfferPrice_Adjust)) end as 'Diff',\r\n\tcase when OfferPrice_Adjust ='0' then '0' else\r\n    ((convert(float,OfferPrice) - convert(float,OfferPrice_Adjust)) / convert(float,OfferPrice) * 100) end as 'PercentDiff',\r\n\tisnull(OfferPrice,'0') as OfferPriceExch,isnull(MinPrice,'0') as MinPriceExch from #temp\r\n\t--update TransFormulaHeader set isactive=1 where requestno=6443 \r\n\t--select * from #temp where requestno=8918 \r\nEND\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetQuotationItems' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetRawMaterial]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetRawMaterial' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetRawMaterial]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\n \r\n-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spGetRawMaterial]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Company nvarchar(max), \r\n\t@RequestNo nvarchar(max),\r\n\t@from datetime,@to datetime\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\t--declare @Company nvarchar(max)='102',@RequestNo nvarchar(max)=214\r\n\t--select * from TransTechnical\r\n\tSET NOCOUNT ON;\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\t--declare @form datetime,@to datetime\r\n\t--select @form=RequestDate,@to=RequireDate from TransTechnical where ID=@RequestNo\r\n\r\n\tselect * into #temp from(\r\n\tselect Material,[Description] from MasPricePolicy where (@from between cast([From] as date) and cast ([To] as date) \r\n\tOr @to between cast([From] as date) and cast ([To] as date)) union\r\n\tselect Material,[Description] from MasPriceStd where (@from between cast([From] as date) and cast ([To] as date)\r\n\tOr @to between cast([From] as date) and cast ([To] as date)) and Company=@Company)#a where substring(Material,1,1) not in ('5')\r\n\t--update MasPricePolicy set [From]='20180131'\r\n\t--Select Company,RawMaterial,count(RawMaterial)'XX' into #temp From MasYield a \r\n\t--Where Company=@Company and substring(a.Material,1,1) not in ('5') group by Company,RawMaterial\r\n\t--insert into #temp select @Company,'',''\r\n\t--Select Company,RawMaterial,\r\n\t--(select top 1 [Description] from MasYield a where a.Company=c.Company and a.RawMaterial=c.RawMaterial)'Description'\r\n\t-- From #temp c\r\n\t--where RawMaterial ='11M420400001'\r\n\tIf Object_ID('tempdb..#table')  is not null  drop table #table\r\n\tselect distinct Material as 'RawMaterial' into #table from #temp order by Material\r\n\r\n\tselect RawMaterial as 'ID',(select top 1 [Description] from #temp b where b.Material=#table.RawMaterial) as [Description]\r\n\tfrom #table order by RawMaterial\r\nEND\r\n\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetRawMaterial' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetReceiver]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetReceiver' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetReceiver]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: