,RequireDate,GETDATE())x,Modified,Customer +'/'+ Destination as 'Customer' into #temp \r\n\t--from TransTechnical Where Company in (select Code from MasPlant where company in (select value from dbo.FNC_SPLIT(@Company,';')) union select value from dbo.FNC_SPLIT(@Company,';')) \r\n\tfrom TransTechnical Where dbo.fnc_checktype(Company,@Plant)>0\r\n\tand StatusApp = 4 --and id not in(select RequestNo from TransCostingHeader where Completed=1)\t\r\n\tand usertype in (select value from dbo.FNC_SPLIT(@usertype,';')) \r\n\tand RequestType in (select value from dbo.FNC_SPLIT(@type,','))\r\n\tand (DATEDIFF(day,Modified,GETDATE()))<360;--CAST(GETDATE() as DATE)\r\n\r\n\t--select *,(DATEDIFF(day,Modified,GETDATE())) from TransTechnical where statusapp=4\r\n\tdeclare @table table(\r\n\tID int,\r\n\tRequestNo nvarchar(max),\r\n\tMarketingNumber nvarchar(max),\r\n\tRequireDate nvarchar(max),\r\n\tPackSize nvarchar(max), \r\n\tPackaging nvarchar(max), \r\n\tRevised int,\r\n\tUserType int,\r\n\tRequestType nvarchar(max),\r\n\tRequestDate nvarchar(max),Modified datetime,Customer nvarchar(max),NetWeight nvarchar(max)\r\n\t)\r\n\tdeclare @RequestNo nvarchar(50), @NetWeight nvarchar(100)\r\n\tdeclare cur_temp CURSOR FOR\r\n\r\n\tSELECT ID,(select top 1 value from dbo.FNC_SPLIT(NetWeight,'|'))'NetWeight' FROM #temp\r\n\r\n\topen cur_temp\r\n\r\n\tFETCH NEXT FROM cur_temp INTO @RequestNo, @NetWeight\r\n\tWHILE @@FETCH_STATUS = 0\r\n\tBEGIN\r\n\t\r\n\t\tinsert into @table\r\n\t\tselect b.ID,\r\n\t\tb.RequestNo,\r\n\t\tb.MarketingNumber,\r\n\t\tb.RequireDate,\r\n\t\tb.PackSize,\r\n\t\tb.Packaging,\r\n\t\tb.Revised,\r\n\t\tb.UserType,\r\n\t\tb.RequestType,\r\n\t\tb.RequestDate,b.Modified,Customer,\r\n\t\tvalue +'|'+ PARSENAME(REPLACE(b.NetWeight,'|','.'),1) from dbo.FNC_SPLIT(@NetWeight,',') a,#temp b\r\n\t\twhere b.ID=@RequestNo\r\n\t\tFETCH NEXT FROM cur_temp INTO @RequestNo, @NetWeight\r\n\tEND\r\n\r\n\tCLOSE cur_temp\r\n\tDEALLOCATE cur_temp\r\n\t--+++\r\n\t--If Object_ID('tempdb..#table')  is not null  drop table #table\r\n\t--select * into #table from @table  \r\n\t--delete @table\r\n\t--select * from #table\r\n\t--declare @ID nvarchar(50), @PackSize nvarchar(100),@countsize nvarchar(max),@NetWeight nvarchar(max)\r\n\t--declare cur_temp CURSOR FOR\r\n\r\n\t--SELECT ID,PackSize,(select count(value) from dbo.FNC_SPLIT(PackSize,',')) as 'countsize',\r\n\t--(select top 1 value from dbo.FNC_SPLIT(NetWeight,'|'))'NetWeight' FROM #temp\r\n\t--open cur_temp\r\n\r\n\t--FETCH NEXT FROM cur_temp INTO @ID, @PackSize,@countsize,@NetWeight\r\n\t--WHILE @@FETCH_STATUS = 0\r\n\t--BEGIN\r\n\t--\tinsert into @table\r\n\t--\tselect b.ID,\r\n\t--\tb.RequestNo,\r\n\t--\tb.MarketingNumber,\r\n\t--\tb.RequireDate,\r\n\t--\tconvert(int,value),\r\n\t--\tb.Packaging,\r\n\t--\tb.Revised,\r\n\t--\tb.RequestType,\r\n\t--\tb.RequestDate,b.Modified,\r\n\t--\tb.NetWeight from dbo.FNC_SPLIT(@PackSize,',') a, #temp b\r\n\t--where b.ID=@ID\r\n\t--FETCH NEXT FROM cur_temp INTO @ID, @PackSize,@countsize,@NetWeight\r\n\t--END\r\n\r\n\t--CLOSE cur_temp\r\n\t--DEALLOCATE cur_temp\r\n\r\n\t--DELETE a\r\n\t--FROM @table a\r\n\t--INNER JOIN TransCostingHeader b\r\n\t--  ON a.ID=b.RequestNo and a.PackSize=b.PackSize\r\n\r\n\tselect ID,\r\n\tRequestNo,\r\n\t--MarketingNumber,\r\n\tRequireDate,\r\n\tisnull(PackSize,'0')'PackSize',\r\n\tisnull(Packaging,'')'Packaging',\r\n\tRevised,\r\n\t--isnull(UserType,'0')UserType,\r\n\tisnull(dbo.fnc_gettype(usertype),'0')Product,\r\n\t--RequestType,\r\n\tRequestDate,\r\n\t--Modified,\r\n\tisnull(Customer,'-')'Customer',\r\n\tisnull(NetWeight,'0')'NetWeight' from @table order by Modified,PackSize,NetWeight desc\r\n\tEND\r\n\r\n \r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetRequestNo' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetRequestNoti]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetRequestNoti' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetRequestNoti]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spGetRequestNoti] \r\n\t-- Add the parameters for the stored procedure here\r\n\t@Id nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\t--declare @Id nvarchar(max)=''\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tdeclare @rd nvarchar(max)\r\n\tset @rd= (\r\n\tselect concat(h.Username,',',replace(h.reason,'|','')) from MasHistory h  where tablename='TransTechnical' and h.StatusApp in (5) and RequestNo =@Id )\r\n\r\n\tselect u.user_name, email,'5' as 'Statusapp' into #temp from ulogin u where u.user_name in (select value from dbo.FNC_SPLIT(@rd,',')) \r\n\tif (select count(*) from #temp )>0\r\n\tselect * from #temp\r\n\telse\r\n\tselect user_name,email,'5' from ulogin where user_name in (select value from dbo.FNC_SPLIT((select (dbo.fnc_getuser(6,Company,UserType)) from TransTechnical where id=@Id and RequestType=1),','))\r\n\r\n\r\nEND\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetRequestNoti' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spGetRequestRate]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spGetRequestRate' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spGetRequestRate]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spGetRequestRate]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@id nvarchar(max),\r\n\t@from datetime,@to datetime\r\nAS\r\nBEGIN\r\n\r\n--declare @id nvarchar(max) = '103|5536A1HGNN01|7991',@from datetime='20210101',@to datetime='20210601'\r\n--exec spRequestRate @id\r\n--set @company =102\r\n--set @material= '2XA1N10000NN-1'\r\n--set @request='1021600003'\r\nIf Object_ID('tempdb..#MasPricePolicy')  is not null  drop table #MasPricePolicy\r\nselect * into #MasPricePolicy from MasPricePolicy where IsActive=0  \r\ndeclare @company nvarchar(50),\r\n\t\t@material nvarchar(50),\r\n\t\t@request nvarchar(50)\r\nDECLARE @tbl TABLE (Value INT,String VARCHAR(MAX))\r\nINSERT INTO @tbl VALUES(1,@id);\r\n--select @company=,@material,@request\r\nselect @company=company,@material=material,@request=request from \r\n(SELECT t3.Value,[1] as company,[2] as material,[3] as request\r\nFROM @tbl as t1\r\nCROSS APPLY [dbo].[DelimitedSplit8K](String,'|') as t2\r\nPIVOT(MAX(Item) FOR ItemNumber IN ([1],[2],[3])) as t3) #a\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\tdeclare @int int,@dt nvarchar(50),@valid datetime,@diff int,@exchangeRat float,@customer nvarchar(50),@usertype nvarchar(max)\r\n\tselect top 1 --@diff=((month(RequireDate) - month(RequestDate))+1), \r\n\t@diff=DATEDIFF(month, RequestDate,RequireDate)+1,\r\n\t@valid=t.RequestDate,\r\n\t@customer=CustomPrice,\r\n\t@usertype=UserType\r\n\tfrom TransTechnical t where