 and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spselectformula]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spselectformula]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Id nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\t--declare @Id nvarchar(max)='16265'\r\n\tSET NOCOUNT ON;\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tselect a.Company,a.MarketingNumber,a.RDNumber,a.PackSize,b.*,\r\n\t(case when (select count(Id) from MasPricePolicy c where c.Material=b.Material and c.Material in (\r\n\tselect Material from MasFixFishCal\t\r\n\t))>0 then 0 else 1 end)'Mark'\r\n\t,convert(float,replace((case when ISNUMERIC(b.priceofunit)=0 then '0' else \r\n\tconvert(float,replace(b.priceofunit,',','')) /(case when Currency='THB' then \r\n\t(case when a.ExchangeRate='0' then '1' else a.ExchangeRate end) else '1' end) end),',',''))'ofunit'\r\n\t,a.ExchangeRate as 'Rate',(case when Currency='THB' then a.ExchangeRate else '1' end) 'XX',\r\n\tconvert(float,(case when ISNUMERIC(replace(b.priceofunit,',',''))=0 then '0' else convert(float,replace(b.priceofunit,',','')) end))'ofunitxx'\r\n\tinto #temp\r\n\tfrom TransFormula b left join TransCostingHeader a on a.ID = b.RequestNo where b.RequestNo = @Id\r\n\r\n\tselect *,\r\n\tcase when Mark=0 then(case Unit when 'KG' then ofunit * 1000 when 'MT' then ofunit end) else 0 end 'Fishprice' from #temp\r\nEND\r\n \r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spselectformula' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spselectformuladisplay]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spselectformuladisplay' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spselectformuladisplay]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spselectformuladisplay]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Id nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\t--declare @Id nvarchar(max)='1038'\r\n\tSET NOCOUNT ON;\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tselect b.Component,\r\n\tb.SubType,\r\n\tb.Description,\r\n\tb.Material,\r\n\tb.Result,\r\n\tb.Yield,\r\n\tb.PriceOfUnit,\r\n    b.Currency,\r\n    b.Unit,\r\n    b.ExchangeRate,\r\n    b.BaseUnit,\r\n    case when b.PriceOfCarton='NaN' or b.PriceOfCarton='Infinity'\r\n\tor b.PriceOfCarton='' then '0' else b.PriceOfCarton end 'PriceOfCarton',\r\n    Formula,\r\n    IsActive into #temp from TransFormula b where b.RequestNo = @Id\r\n\tIf Object_ID('tempdb..#table')  is not null  drop table #table\r\n\tselect * into #table from #temp where Component ='Raw Material' \r\n\t--select * from #temp\r\n\t--ingredients\r\n\tinsert into #table(Component,SubType,[Description],Result,PriceOfCarton,Formula,IsActive)\r\n\tselect Component,SubType,SubType,\r\n\tsum(convert(Numeric,Result))'Result',\r\n\tsu