e from #appr where levelApp=1 and tablename=0 and #appr.RequestNo=#temp.trfid) end as 'Received',\r\n\tcase when substring(#temp.RequestNo,4,1)='1' then #temp.CreateOn else (select SubmitDate from #appr where levelApp=6 and tablename=0 and #appr.RequestNo=#temp.trfid) end as 'PD',\r\n\tcase when substring(#temp.RequestNo,4,1)='1' then #temp.CostStarted else (select SubmitDate from #appr where levelApp=2 and tablename=0 and #appr.RequestNo=#temp.trfid) end as 'Started',\r\n\t(select top 1 SubmitDate from #apprcost where levelApp=2 and tablename in (1,2) and #apprcost.RequestNo=#temp.ID) as 'Completed',\r\n\t(select SubmitDate from #apprcost where levelApp=3 and tablename in (1,2) and #apprcost.RequestNo=#temp.ID) as 'SendCostMKT',\r\n\tRequestNo as 'TRFWebBase',(select top 1 NamingCode from MasPlant b where dbo.fnc_checktype(b.usertype,#temp.usertype)>0 and b.Company=#temp.Company)as 'Plant',--u.FirstName +'.'+ substring(u.LastName,1,1) 'Marketing Name',\r\n\t#temp.Requester as 'MarketingName',\r\n\t(case when code=name then '' else code end)'ProductName' into #t from #temp \r\n\t--select * from #t\r\n\r\n\tselect trfid as ID,\r\n\tRequestNo,\r\n\tRequestfor,\r\n\tPetFoodType,\r\n\tProposedFactory,\r\n\tCostNo as 'CostingNo',\r\n\tcase when substring(code,1,1)='3' then Code end 'MaterialCode',\r\n\tRefSamples as 'TestNo',\r\n\tPlant,\r\n\tPackaging as 'Package',\r\n\tCanSize,\r\n\t(select top 1 value from dbo.FNC_SPLIT(#t.Netweight,'|')) as 'NW',\r\n\tName as 'ProductName',\r\n\tCustomer,\r\n\tupper(MarketingName)'MarketingName',\r\n\tDestination as 'Country',\r\n\tMarketing,\r\n\tReceived 'R&D',\r\n\tPD,\r\n\t[ESTVolume],\r\n\t[ESTLaunching],\r\n\t[ESTFOB],\r\n\t[Started],\r\n\tCompleted,\r\n\tcase when Completed != '' or [Started] != '' then \r\n\t(select convert(nvarchar(max),[Days])+'.'+ convert(nvarchar(max),[Hours]) from dbo.fnc_DateDif([Started],Completed)) end as 'Usedtime',\r\n\tStatusApp,\r\n\tRemark,\r\n\tupper(CreateBy) as 'By',\r\n\tSendCostMKT\r\n\t,case when SendCostMKT !='' or [Started]!='' then \r\n\t(select convert(nvarchar(max),[Days])+'.'+ convert(nvarchar(max),[Hours]) from dbo.fnc_DateDif([Started],SendCostMKT))end as 'KPIUsedtime'\r\n\tfrom #t\r\nEND\r\n--select * from TransTechnical where id=7222\r\n--select * from TransApprove where requestno=19344\r\n--select * from mashistory where requestno=19344\r\n--select * from(SELECT [user_name],firstname +' '+ lastname as fn from ulogin where IsResign=0 and firstname like 'manee%' )#a union select '0','None'\r\n--select * from transcostingheader where marketingNumber='TU630049'\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryCostingsheet' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spSummaryCostSec]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryCostSec' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spSummaryCostSec]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE  [dbo].[spSummaryCostSec]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@UserNo nvarchar(max),\r\n\t@ID nvarchar(max)\r\nAS\r\nBEGIN\r\n\r\n--declare @Keyword nvarchar(max)='[CreateOn] Between ''2020-03-01'' and ''2020-03-31''',@UserNo nvarchar(max)='FO5910155',@ID nvarchar(max)='12580'\r\n\tIf Object_ID('tempdb..#TransCosting')  is not null  drop table #TransCosting\r\n\tselect * into #TransCosting from TransCosting where RequestNo in (select value from dbo.FNC_SPLIT(@ID,',')) \r\n\tand LOWER(component) in ('secondary packaging','primary packaging') \r\n\t--select * from #TransCosting\r\n\tIf Object_ID('tempdb..#tempClass')  is not null  drop table #tempClass\r\n\tselect a.RequestNo,a.Formula,a.Amount,b.Description as 'Name' into #tempClass from #TransCosting a left join MaterialClass b on substring(a.SAPMaterial,2,1)=b.Id \r\n\t\r\n\t\r\n \r\n\tdeclare @cols nvarchar(max),@query nvarchar(max)\r\n\tSET @cols = STUFF((SELECT distinct ',' + QUOTENAME(c.Name) \r\n            FROM #tempClass c\r\n            FOR XML PATH(''), TYPE\r\n            ).value('.', 'NVARCHAR(MAX)') \r\n        ,1,1,'')\r\nset @query = N'SELECT requestno,Formula,' + @cols + ' from \r\n            (\r\n                select requestno,Formula,Amount,Name\r\n\r\n                from #tempClass\r\n           ) x\r\n            pivot \r\n            (\r\n                 min(Amount)\r\n                for Name in (' + @cols + ')\r\n            ) p '\r\n\r\nexecute (@Query)\r\nEND\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryCostSec' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spSummaryCustomer]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryCustomer' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spSummaryCustomer]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spSummaryCustomer]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Keyword nvarchar(max),\r\n\t@UserNo nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\t\r\n\t--declare @Keyword nvarchar(max)='[CreateOn] Between ''2022-01-01'' and ''2022-03-31''',@UserNo nvarchar(max)='MO330303'\r\n \tdeclare @usertype nvarchar(max)\r\n\tset @usertype =(select usertype from ulogin where [user_name]=@UserNo)\r\n\tdeclare @str nvarchar(max)\r\n\tIf Object_ID('tempdb..#find')  is not null  drop table #find\r\n\tselect * into #find from dbo.FindULevel(@UserNo)\r\n\tdeclare @temp tabletype;\r\n\tdelete @temp\r\n\tinsert into @temp\r\n\tselect * from(select editor from #find)#a\r\n\tdeclare @editor nvarchar(max)= dbo.fnc_stuff(@temp)\r\n\r\n\tset @str='select ID,RequestNo,ExchangeRate,CreateOn from TransCostingHeader where RequestNo <>0 and dbo.fnc_checktype(usertype,'''+@usertype+''')>0'\r\n\tSET NOCOUNT ON;\r\n\tIf Object_ID('tempdb..#ULevel')  is not null  drop table #ULevel\r\n\tselect * into #ULevel from dbo.FindULevel(@UserNo) where idx in(0,1,2,5,9,10)\r\n\r\n\tIf Object_ID('tempdb..#table')  is not null  drop table #table\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\tIf Object_ID('tempdb..#TransFormula')  is not null  drop table #TransFormula\r\n\tIf Object_ID('tempdb..#TransCosting')  is not null  drop table #TransCosting\r\n\tif OBJECT_ID('tempdb..#TransFormulaHeader') is not null drop table #TransFormulaHeader\r\n\tif OBJECT_ID('tempdb..#LBRate') is not null drop table #LBRate\r\n\tif OBJECT_ID('tempdb..#TransTechnical') is not null drop table #TransTechnical\r\n    -- Insert statements for procedure here\r\n\t--update TransCosting set per=0 where per = '' and Component in ('margin')\r\n\tIf Object_ID('tempdb..#query')  is not null  drop table #query\r\n\tdeclare @sql NVARCHAR(MAX)\r\n\tdeclare @table Table(ID nvarchar(max),RequestNo nvarchar(max), ExchangeRate float,CreateOn datetime)\r\n\tif(select count(*) from #ULevel where dbo.fnc_checktype(SubApp,'0,1')>0)>0\r\n\tbegin\r\n\t\tinsert into @table\r\n\t\tselect ID,RequestNo,ExchangeRate,CreateOn from TransCostingHeader where statusapp in (0) and RequestNo in (\r\n\t\tselect ID from TransTechnical a \r\n\t\twhere (dbo.fnc_checktype(a.Requester ,@editor)>0 or (select count(value) from dbo.FNC_SPLIT(a.Assignee,',') where dbo.fnc_checktype(value ,@editor)>0)>0)) \r\n\tend\r\n\telse\r\n\tbegin\r\n\tSET @sql = case when @Keyword='X' or @Keyword='' then \r\n\t@str +' and statusapp in (3,4,0,8) and (DATEDIFF(day,CreateOn,GETDATE()))<31' \r\n\telse @str +' and statusapp in (3,4,0,8) and ' +' ' + @Keyword end\r\n\t--print @sql;\r\n\tinsert into @table EXEC sp_executesql @sql\r\n\tend\r\n\t--declare @Id nvarchar(max)='8918'\r\n\tselect *,(case when ISNUMERIC(LBRate)=0 then '0' else replace(LBRate,',','') end)'LBRatex',\r\n\t(case when ISNUMERIC(Result)=0 then '0' else replace(Result,',','') end)'Resultx',\r\n\t(case when ISNUMERIC(priceofcarton)=0 then '0' else replace(priceofcarton,',','') end)'priceofcartonx',\r\n\t(case when ISNUMERIC(priceofunit)=0 then '0' else replace(priceofunit,',','') end)'priceofunitx'\r\n\t into #TransFormula from TransFormula where RequestNo in (select ID from @table)\r\n\tselect * into #TransCosting from TransCosting where RequestNo in (select ID from @table)\r\n\tselect * into #TransTechnical from TransTechnical where ID in (select RequestNo from @table)\r\n\tselect * into #TransFormulaHeader from TransFormulaHeader where RequestNo in (select ID from @table)\r\n\tselect * into #table from @table\r\n\r\n\tselect Formula,RequestNo,sum(\r\n\tconvert(float,a.LBRatex)*convert(float,Resultx))/((select sum(convert(float,x.Resultx)) \r\n\tfrom #TransFormula x where x.Formula=a.Formula and x.LBRate<>'' and x.Resultx<>'0' and RequestNo=a.RequestNo))'LBRate' \r\n\tinto #LBRate FROM #TransFormula a where  ISNUMERIC(a.LBRate)=1 and a.Resultx<>'0' group by a.Formula,a.RequestNo\r\n\t--select * from #LBRate where Result='0'\r\n\t--select a.*,(case when c.material is null then '1' else '0' end) as 'Mark'  into #MasPricePolicy \r\n\t--from #TransFormula a left join MasPricePolicy c on a.Material=c.Material where c.Material in (select Material from #TransFormula)\r\n\r\n\tselect * into #temp from (select Id,RequestNo,Formula,convert(float,LTRIM(RTRIM(priceofcartonx))) as 'minprice',\r\n\t(case when Component in ('Raw Material') then 'RM' else 'Ing' end) as 'CompoType',Unit from #TransFormula where RequestNo in (select ID from @table) union \r\n\tselect Id,RequestNo,Formula,convert(float,\r\n\t(case when ISNUMERIC(LTRIM(RTRIM(priceofunitx)))=0 then '0' else convert(float,LTRIM(RTRIM(priceofunitx))) /(case when Currency='THB' then \r\n\t(select ExchangeRate from @table x where x.ID=#TransFormula.RequestNo) else 1 end) end))'ofunit',\r\n\t(case when #TransFormula.Material in ('11M1SL004001','11M1YL004001','11M210000001','11M1B0000002') then 'X' else '1' end)'Fishprice',\r\n\tUnit from #TransFormula where RequestNo in (select ID from #table) union\r\n\t--select * from #TransFormula where RequestNo in (select ID from @table) and LBRate <> '' group by Id,RequestNo,Formula union\r\n\t\r\n\tselect id,RequestNo,Formula,(case when Component in ('margin') then \r\n\tconvert(float,(case when ISNUMERIC(LTRIM(RTRIM(per))) = 0 then '0' else LTRIM(RTRIM(per)) end)) \r\n\twhen Component='Labor&Overhead' and Description='Labelling & WH DL' then Quantity\r\n\twhen Component='Labor&Overhead' and Description='LOH Factor Adjustment' then Quantity\r\n\telse \r\n\tconvert(float,(isnull((case when ISNUMERIC(LTRIM(RTRIM(Quantity))) = 0 then '0' else LTRIM(RTRIM(Quantity)) end),'0'))) * \r\n\tconvert(float,(isnull((case when ISNUMERIC(LTRIM(RTRIM(priceUnit))) = 0 or priceUnit='-' then '0' else LTRIM(RTRIM(priceUnit)) end),'0'))) end),Component,Description \r\n\tfrom #TransCosting where RequestNo in (select ID from @table)\r\n\t )#a\r\n\t--select * from #TransCosting\r\n\t--select cast((sum(case when CompoType in ('RM') then minprice else 0 end)) as decimal(10,2))  from #temp where minprice is null\r\n\tIf Object_ID('tempdb..#total')  is not null  drop table #total\r\n\tselect #a.RequestNo,#a.Formula,b.ID,Customer\r\n\t,CostNo,\r\n\tcase when substring(b.code,1,1)='3' then Code end 'MaterialCode',\r\n\tb.RefSamples,\r\n\tb.Name as 'ProductName',\r\n\t#a.RM,\r\n\t#a.Ing,\r\n\t#a.Margin,\r\n\t#a.PrimaryPkg,\r\n\t#a.SecondaryPkg,\r\n\t#a.[Labelling & WH DL],\r\n\t#a.[LOH Factor Adjustment],\r\n\t--#a.LOH,\r\n\t#a.UpCharge,#a.Fishprice into #total from (\r\n\tselect RequestNo,Formula, cast((sum(case when CompoType in ('RM') then minprice else 0 end)) as float)as 'RM',\r\n\tcast((sum(case when CompoType in ('Ing') then minprice else 0 end)) as float) as 'Ing',\r\n\tcast((sum(case when CompoType in ('margin') then minprice else 0 end)) as float) as 'Margin',\r\n\tcast((sum(case when CompoType in ('primary packaging') then minprice else 0 end)) as float) as 'PrimaryPkg',\r\n\tcast((sum(case when CompoType in ('secondary packaging') then minprice else 0 end)) as float) as 'SecondaryPkg',\r\n--\tcast((sum(case when CompoType in ('LOH') then minprice else 0 end)) as float) as 'LOH',\r\n\tcast((sum(case when CompoType in ('Labor&Overhead') and Unit='Labelling & WH DL' then minprice else 0 end)) as float) as 'Labelling & WH DL',\r\n\tcast((sum(case when CompoType in ('Labor&Overhead') and Unit='LOH Factor Adjustment' then minprice else 0 end)) as float) as 'LOH Factor Adjustment',\r\n\tcast((sum(case when CompoType in ('upcharge') then minprice else 0 end)) as float) as 'UpCharge',\r\n\tcast((max(case when CompoType in ('X') then \r\n\t(case Unit when 'KG' then minprice * 1000 when 'MT' then minprice end)\r\n\t else 0 end)) as float) as 'Fishprice' from #temp where RequestNo in (select ID from @table)\r\n\tgroup by RequestNo,Formula)#a left join #TransFormulaHeader b on #a.RequestNo=b.RequestNo and #a.Formula=b.Formula \r\n\t--select ISNUMERIC('0.63x')\r\n\t--select NEWID()\r\n\t\r\n--EXEC dbo.spDynamicPivot @Query = 'SELECT * FROM ##MyTable', @AggregateFunction = 'min(Amount)', @ValueColumn = 'Name', @ResultTable = '#MyTable'\r\n\r\n\tselect ROW_NUMBER() OVER (\r\n\tORDER BY #a.ID\r\n   ) ID,\r\n\t#a.RequestNo,\r\n\t#a.Formula,\r\n\t#a.ID as row_num,\r\n\t#a.CostNo,\r\n\tisnull((case when #a.Customer='' or #a.Customer is null then \r\n\tc.Customer else #a.Customer end),'') 'Customer',\r\n\t#a.MaterialCode,\r\n\t#a.ProductName,\r\n\t#a.RefSamples,\r\n\t#a.RM,\r\n\t#a.Ing,\r\n\t#a.PrimaryPkg,\r\n\t#a.SecondaryPkg,\r\n\t#a.LOH,\r\n\t#a.UpCharge,\r\n\t#a.RmIng,\r\n\t#a.Loss,\r\n\t#a.Margin,\r\n\t#a.FOB,\r\n\t#a.FOBUsd,\r\n\t#a.PerMargin,#a.ExchangeRate,#a.Fishprice,#a.CreateOn,upper(#a.name)name\r\n\t,c.RequestNo 'TechnicalNo',c.RequestDate,c.RequireDate,#a.packsize,#a.Packaging,#a.CanSize from (select \r\n\ta.RequestNo,--b.RequestNo as 'XX',\r\n\t--(select #TransTechnical.RequestNo from #TransTechnical where #TransTechnical.ID=b.RequestNo) as 'TechnicalNo',\r\n\tFormula,\r\n\ta.ID,\r\n\tCostNo,Customer,\r\n\t--isnull((case when Customer='' or Customer is null then \r\n\t--(select #TransTechnical.Customer from #TransTechnical where #TransTechnical.ID=b.RequestNo) else Customer end),'') 'Customer',\r\n\tisnull(MaterialCode,'') 'MaterialCode',\r\n\tProductName,\r\n\ta.RefSamples,\r\n\ta.RM,\r\n\ta.Ing,\r\n\ta.PrimaryPkg,\r\n\ta.SecondaryPkg,\r\n\t--isnull((select LBRate from #LBRate where #LBRate.Formula=a.Formula and #LBRate.RequestNo=a.RequestNo),'0') as 'LOH',\r\n\t(isnull((select LBRate from #LBRate where #LBRate.Formula=a.Formula and #LBRate.RequestNo=a.RequestNo),'0')\r\n\t+ (case when len(a.[LOH Factor Adjustment])>0 then (a.[LOH Factor Adjustment]/100)*\r\n\t(isnull((select LBRate from #LBRate where #LBRate.Formula=a.Formula and #LBRate.RequestNo=a.RequestNo),'0')) else 1 end)+a.[Labelling & WH DL])\tas 'LOH',\r\n\ta.UpCharge,\r\n\tRM+Ing as 'RmIng',\r\n\tconvert(float,0) as Loss,\r\n\tconvert(float,0) as 'Margin',\r\n\tconvert(float,0)  as 'FOB',\r\n\tconvert(float,0)  as 'FOBUsd',\r\n\ta.Margin as 'PerMargin',ExchangeRate,a.Fishprice,b.CreateOn,isnull(dbo.fnc_getcd(b.RequestNo),'')'Name',b.RequestNo TechnicalID,\r\n\t--,format(isnull(((select sum(convert(float,isnull(p.PriceOfUnit,0))) from #MasPricePolicy p where p.Formula=#total.Formula and p.RequestNo=#total.RequestNo) * 1000)/\r\n\t--(case when len(ExchangeRate)=0 or ExchangeRate = null then 1 else convert(float,ExchangeRate) end),'0') ,'#,###.00')as 'Fishprice'\t\r\n\t(select top 1 aa.packsize from TransCostingHeader aa where aa.id =a.RequestNo) packsize,\r\n\t(select top 1 aa.Packaging from TransCostingHeader aa where aa.id =a.RequestNo) Packaging,\r\n\t(select top 1 aa.CanSize from TransCostingHeader aa where aa.id =a.RequestNo) CanSize\r\n\tfrom #total a left join #table b on a.RequestNo=b.ID)#a left join #TransTechnical c on c.ID=#a.TechnicalID\r\nEND\r\n \r\n --select * from ulogin where FirstName like 'chu%'\r\n","GO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryCustomer' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spsummaryFormula]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spsummaryFormula' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spsummaryFormula]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spsummaryFormula]\r\n\t-- Add the parameters for the stored procedure here\r\n@Id nvarchar(max),\r\n@username nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\r\n    -- Insert statements for procedure here\r\n\tSELECT a.* from TransCostingHeader a left join TransTechnical b on b.ID=a.RequestNo where a.ID=@Id\r\nEND\r\n\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spsummaryFormula' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spsummaryFormulaall]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spsummaryFormulaall' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spsummaryFormulaall]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spsummaryFormulaall]\r\n\t-- Add the parameters for the stored procedure here\r\n@user_name nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\r\n    -- Insert statements for procedure here\r\n\tSELECT a.ID,a.Company,\r\n\ta.MarketingNumber,\r\n\ta.RDNumber,\r\n\ta.StatusApp,\r\n\ta.CanSize,\r\n\ta.PackSize,\r\n\tb.Customer,\r\n\tb.RequestNo from TransCostingHeader a left join TransTechnical b on b.ID=a.RequestNo --where a.ID=1040\r\nEND\r\n\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spsummaryFormulaall' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spSummaryQuotation]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryQuotation' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spSummaryQuotation]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","CREATE PROCEDURE [dbo].[spSummaryQuotation]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@UserNo nvarchar(max),\r\n\t@from datetime,\r\n\t@to datetime,\r\n\t@material nvarchar(max),\r\n\t@requestno nvarchar(max),\r\n\t@costing nvarchar(max),\r\n\t@statusapp nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\t--select * from ulogin where email like '%juthamard.aksorn@thaiunion.com%'\r\n\t--declare @UserNo nvarchar(max)='MO610314',@from datetime='20210220',@to datetime='20220324',@material nvarchar(max)='',@requestno nvarchar(max)='',@statusapp nvarchar(max)='2',@costing nvarchar(max)=''\r\n\tdeclare @Keyword nvarchar(max)='convert(nvarchar(max),cast([CreateOn] as date)) Between '''+ convert(nvarchar(max),cast(@from as date))+''' and '''+ convert(nvarchar(max),cast(@to as date))+''''\r\n\t--print @Keyword;\r\n\tIf Object_ID('tempdb..#find')  is not null  drop table #find\r\n\tselect * into #find from dbo.FindULevel(@UserNo)\r\n\t--select * from #find\r\n\tSET NOCOUNT ON;\r\n\tdeclare @temp tabletype;\r\n\tdelete @temp\r\n\tinsert into @temp\r\n\tselect * from(select editor from #find)#a\r\n\tdeclare @editor nvarchar(max)= dbo.fnc_stuff(@temp)\r\n\r\n\tprint @editor;\r\n\tdeclare @str nvarchar(max)\r\n\tdeclare @table Table(ID nvarchar(max),RequestNo nvarchar(max),CreateOn datetime)\r\n\tset @str=N'select ID,RequestNo,CreateOn from TransQuotationHeader where dbo.fnc_checktype(CreateBy,'''+@editor+''')>0'\r\n\tdeclare @sql NVARCHAR(MAX)\r\n\tSET @sql = CONCAT( @str ,' and requestno in (select LTRIM(RTRIM(value)) from dbo.FNC_SPLIT((case when '''+@requestno+'''<>'''' then '''+@requestno+''' else requestno end),'','')) and statusapp in (3,4,0,8,9) and ' , @Keyword ) \r\n\tprint @sql;\r\n\tinsert into @table EXEC sp_executesql @sql\r\n\tIf Object_ID('tempdb..#Qtemp')  is not null  drop table #Qtemp\r\n\r\n    -- Insert statements for procedure here\r\n\tSELECT a.*,b.RequestNo as Quotation,b.Customer,b.ShipTo,b.StatusApp 'Status',b.Currency,\r\n\tb.Incoterm,\r\n\t(select CONCAT(Code +':',Value) from MasPaymentTerm where Code=b.PaymentTerm) as 'PaymentTerm'\r\n\tinto #Qtemp from TransQuotationItems a left join TransQuotationHeader b on b.ID=a.SubID\r\n\twhere a.SubID in (select ID from @table)\r\n\t\r\n\tIf Object_ID('tempdb..#TransCostingHeader')  is not null  drop table #TransCostingHeader\r\n\tselect b.ID,(select top 1 RequestNo from TransTechnical a where a.ID=b.RequestNo)TechnicalNo,\r\n\t(select top 1 RequestDate from TransTechnical a where a.ID=b.RequestNo)[Form],\r\n\t(select top 1 RequireDate from TransTechnical a where a.ID=b.RequestNo)[To] into #TransCostingHeader\r\n\tfrom TransCostingHeader b where id in (select RequestNo from #Qtemp) \r\n\t--and b.StatusApp = case when (select count(*) from dbo.FNC_SPLIT(@statusapp,',') where value in (3))>0 then 4 else b.StatusApp end \r\n\t--select * from TransQuotationHeader\r\n\tselect * from(\r\n\tselect ROW_NUMBER() OVER (\r\n\tORDER BY #a.ID\r\n   ) row_num,#a.ID,\r\n\t#a.Customer as 'Sold-To-Code',\r\n\t#a.Customer,\r\n\t#a.ShipTo as 'Ship-To-Code',\r\n\t#a.ShipTo,#a.Incoterm,#a.PaymentTerm,\r\n\t(case  SUBSTRING(c.CostNo,1,2) when 'PF' then '103' \r\n\twhen 'GP' then '102' end )'sale Org',\r\n\t(case when #a.RequestType='V' then (select h.VarietyPack from TransCostingHeader h where h.ID=#a.requestno) else\r\n\tc.Code end) as 'Code',c.Name,#a.OfferPrice_Adjust,format(#a.Form,'dd/MM/yyyy')'Form',format([To],'dd/MM/yyyy') as 'To',\r\n\t#a.MinPrice,\r\n\t#a.OfferPrice,\r\n\t\r\n\t#a.ActionDate,\r\n\tcase when convert(nvarchar(max),#a.StatusApp)='A' then 'Below Cost' else '-' end 'StatusApp',\r\n\tcase #a.IsActive when '4' then 'Send To complete' else '' end 'IsActive',\r\n\t#a.Quotation,\r\n\t#a.TechnicalNo 'requestno',\r\n\t\r\n\t\r\n\t#a.SubID,\r\n\tc.Formula,\r\n\t(select s.Title from MasStatusApp s where s.id=isnull(#a.Status,0) and s.levelapp in (3,2))'StatusName',\r\n\t(case when isnull(#a.Status,0)=4 then (select max(h.createon) from MasHistory h where h.tablename='TransQuotationHeader' and h.StatusApp in (1,4) and h.RequestNo=#a.SubID) else null end)  [complete process],\r\n\tisnull(c.CostNo,'') as CostNo,\r\n\t#a.Currency\r\n\tfrom(\r\n\tselect a.*,b.TechnicalNo,b.Form,[To] from #Qtemp a left join #TransCostingHeader b on b.ID=a.RequestNo\r\n\twhere isnull(a.IsActive,0) = case when (select count(*) from dbo.FNC_SPLIT(@statusapp,',') where value in (1))>0 then 4 \r\n\twhen (select count(*) from dbo.FNC_SPLIT(@statusapp,',') where value in (2))>0 then 0 else a.IsActive end \r\n\t)#a left join TransFormulaHeader c on c.ID=convert(int,#a.CostingNo)\r\n\twhere isnull(#a.Quotation,'')<>'')#b where CostNo like '%' +  (case when len(@costing)>0 then @costing else CostNo end)  +'%' and Code like '%' + (case when len(@material)>0 then @material else Code end) +'%'\r\nEND\r\n \r\n","GO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryQuotation' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spSummaryReport]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryReport' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spSummaryReport]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spSummaryReport]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Keyword nvarchar(max),\r\n\t@UserNo nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\t--declare @Keyword nvarchar(max)='',@UserNo nvarchar(max)='fo5910155'\r\n\tSET NOCOUNT ON;\r\n\tIf Object_ID('tempdb..#tt')  is not null  drop table #tt\r\n\tdeclare @usertype nvarchar(max)\r\n\tset @usertype =(select usertype from ulogin where [user_name]=@UserNo)\r\n \tselect * into #tt from TransTechnical  where usertype in (select value from dbo.FNC_SPLIT(@usertype,';'))\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n    -- Insert statements for procedure here\r\n\tSELECT ID,RequestNo,isnull(Customer,'-')Customer,\r\n\tisnull(Destination,'-')Destination,\r\n\t--(case when (select count(Sublevel) from dbo.FindULevel(Requester) where Sublevel in (1,2))>0 then isnull(Assignee,'')+'|'+Requester\r\n\t--else Requester +''+ isnull(+'|'+Assignee,'')end)name,\r\n\tRequester +''+ replace(isnull(+'|'+Assignee,''),',','|') name,\r\n\tCompany,Notes,StatusApp,CreateOn,upper(Requester)'Requester',UserType into #temp\r\n\tfrom #tt \r\n\r\n\t--select * from #temp\r\n\tIf Object_ID('tempdb..#t')  is not null  drop table #t\r\n\tselect Packaging,Formula,a.RequestNo into #t from TransCostingHeader a left join TransFormula b \r\n\ton b.RequestNo=a.ID where a.RequestNo in (select ID from #temp) and a.usertype in (select value from dbo.FNC_SPLIT(@usertype,';')) group by a.Packaging,b.Formula,a.RequestNo\t\r\n\tIf Object_ID('tempdb..#formula')  is not null  drop table #formula\r\n\tCREATE TABLE #formula(\r\n\tRequestNo INT primary key ,\r\n\tformula int\r\n\t);\r\n\tinsert into #formula\r\n\tselect RequestNo,count(formula) formula from #t group by RequestNo\r\n\tIf Object_ID('tempdb..#query')  is not null  drop table #query\r\n\tselect ID,\r\n\tRequestNo,\r\n\tupper(replace(name,'|',',')) as 'name',\r\n\tcharindex('|',name) as strindex,\r\n\tupper(case when charindex('|',name)=0 then name else SUBSTRING(name,0,charindex('|',name))end) as CD, \r\n\tupper(replace((case when charindex('|',name)=0 then '' else SUBSTRING(name,charindex('|',name)+1,len(name)+1) end),'|',',')) as PMC,\r\n\t--Customer+'/'+@Keyword as 'Customer',\r\n\tCustomer as 'Customer',\r\n\tDestination,\r\n    dbo.RemoveAllSpaces(replace((case when len(Company)=3 then (select Name from MasCompany where Code=#temp.Company) \r\n\telse (select Title from MasPlant where Code=#temp.Company and dbo.fnc_checktype(usertype,#temp.usertype)>0) end),'Plant','')) Company,\r\n\tNotes,\r\n\t(select count(#formula.formula) from #formula where #formula.RequestNo=#temp.ID) as formula,\r\n\tStatusApp,CreateOn,Requester into #query\r\n\tfrom #temp \r\n\t--select * from #formula\r\n\tdeclare @sql NVARCHAR(MAX)\r\n\tSET @sql = case when @Keyword='X' or @Keyword='' then 'select * from #query where (DATEDIFF(day,CreateOn,GETDATE()))<31' \r\n\telse 'select * from #query where' +' ' + @Keyword end\r\n\tprint @sql;\r\n\tEXEC sp_executesql @sql\r\nEND\r\n--select (DATEDIFF(day,'2017-11-01',GETDATE()))\r\n","GO\r\n"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryReport' and @Schema='dbo']","object_type":"StoredProcedure"}},{"cell_type":"markdown","source":["# [dbo].[spSummaryReportCost]"],"metadata":{"urn":"Server[@Name='IDC-APP-HV1-197']/Database[@Name='DevCostingSheet']/StoredProcedure[@Name='spSummaryReportCost' and @Schema='dbo']","object_type":"StoredProcedure"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  StoredProcedure [dbo].[spSummaryReportCost]    Script Date: 19-06-2023 8:50:02 AM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","-- =============================================\r\n-- Author:\t\t<Author,,Name>\r\n-- Create date: <Create Date,,>\r\n-- Description:\t<Description,,>\r\n-- =============================================\r\nCREATE PROCEDURE [dbo].[spSummaryReportCost]\r\n\t-- Add the parameters for the stored procedure here\r\n\t@Keyword nvarchar(max),\r\n\t@UserNo nvarchar(max)\r\nAS\r\nBEGIN\r\n\t-- SET NOCOUNT ON added to prevent extra result sets from\r\n\t-- interfering with SELECT statements.\r\n\tSET NOCOUNT ON;\r\n\t--declare @Keyword nvarchar(max)='X',@UserNo nvarchar(max)='mp001688'\r\n\t--declare @Keyword nvarchar(max)='CreateOn between ''2020-02-09'' and ''2023-03-10''',@UserNo nvarchar(max)='fo5910155'\r\n\tdeclare @usertype nvarchar(max)\r\n\tset @usertype =(select usertype from ulogin where [user_name]=@UserNo)\r\n\tIf Object_ID('tempdb..#level')  is not null  drop table #level\r\n\tselect * into #level from dbo.FindULevel(@UserNo) where idx in(0,1,2,5,9,10)\r\n\tdeclare @t tabletype \r\n\tinsert into @t\r\n\tselect case when Sublevel in (6,7) then concat(ulevel,',',editor) else editor end from #level\r\n\tdeclare @editor nvarchar(max)=dbo.fnc_stuff(@t)\r\n\tdeclare @str nvarchar(max)='select ID from TransCostingHeader where (CreateBy In (select distinct value from dbo.FNC_SPLIT('''+@editor+''','','')) or ID in (select distinct(RequestNo) from MasHistory where Username'\r\n\tset @str= @str + ' in (select distinct value from dbo.FNC_SPLIT('''+@editor+''','','')))) and dbo.fnc_checktype(usertype,'''+@usertype+''')>0'\r\n\tIf Object_ID('tempdb..#temp')  is not null  drop table #temp\r\n\t\r\n\tdeclare @sql NVARCHAR(MAX)\r\n\tdeclare @table Table(ID nvarchar(max))\r\n\tSET @sql = case when @Keyword='X' or @Keyword='' then \r\n\t@str +' and (DATEDIFF(day,CreateOn,GETDATE()))<31' \r\n\telse @str +' and ' +' ' + @Keyword end\r\n\t--print @sql;\r\n\tinsert into @table EXEC sp_executesql @sql\r\n \r\n    -- Insert statements for procedure here\r\n\tSELECT #a.*,isnull(b.CostNo,#a.MarketingNumber) as 'CostNo',isnull(b.Code,'')as 'Code',b.Name,isnull(b.ID,#a.ID) as 'Items',b.RefSamples into #temp \r\n\tfrom(select b.RequestNo,a.ID,a.Packaging,a.Netweight,a.CanSize,a.Company,b.Requester,b.ID as 'trfid',a.Revised,b.CreateOn,a.CreateOn as 'CostStarted',\r\n\tb.Destination,a.StatusApp,a.Remark,a.CreateBy,a.MarketingNumber,a.UserType,a.Customer \r\n\tfrom TransCostingHeader a left join TransTechnical b on a.RequestNo=b.ID where a.RequestNo > 0 and a.ID in (select * from @table) \r\n\tand dbo.fnc_checktype(a.usertype,@usertype)>0\r\n\t)#a left join TransFormulaHeader b on #a.ID=b.RequestNo\r\n\t--select * from #temp\r\n\t--If Object_ID('tempdb..#ulog')  is not null  drop table #ulog\r\n\t--select * from  MasHistory where statusapp=1\r\n\tIf Object_ID('tempdb..#appr')  is not null  drop table #appr\r\n\tselect * into #appr from TransApprove where RequestNo in (select distinct trfid from #temp) and tablename='0' and levelApp in (1,2)\r\n\tIf Object_ID('tempdb..#apprcost')  is not null  drop table #apprcost\r\n\tselect * into #apprcost from TransApprove where RequestNo in (select distinct id from #temp) and tablename='1' and levelApp in (2,3)\r\n\tIf Object_ID('tempdb..#history')  is not null  drop table #history\r\n\tselect * into #history from MasHistory where RequestNo in (select distinct trfid from #temp) and tablename in ('1','TransTechnical') \r\n\t--calculate\r\n\tIf Object_ID('tempdb..#t')  is not null  drop table #t\r\n\tselect #temp.*,\r\n\tcase when substring(#temp.RequestNo,4,1)='1' then #temp.CreateOn else (select SubmitDate from #appr where levelApp=1 and tablename=0 and #appr.RequestNo=#temp.trfid) end as 'Received',\r\n\tcase when substring(#temp.RequestNo,4,1)='1' then #temp.CostStarted else (select SubmitDate from #appr where levelApp=2 and tablename=0 and #appr.RequestNo=#temp.trfid) end as 'Started',\r